var navlinks=document.getElementsByClassName("navlink");function navLink(){for(var a=0;a<navlinks.length;a++)navlinks[a].addEventListener("click",navChange);}function navChange(){var a=event.target;document.querySelector(".menu li.active").classList.remove("active");a.parentElement.classList.add("active");document.querySelector("section.select").classList.remove("select");document.querySelector("section[data-nav="+a.dataset.nav+"]").classList.add("select");}
document.onreadystatechange=function(){"complete"==document.readyState&&navLink();};